import React from 'react';
import { FaGithub, FaLinkedin, FaTwitter, FaEnvelope, FaArrowUp } from 'react-icons/fa';
import './Footer.css';

const Footer = () => {
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const currentYear = new Date().getFullYear();

  return (
    <footer className="footer">
      <div className="footer-container">
        
        {/* Brand / About Section */}
        <div className="footer-about">
          <h3>Kimutai<span>.dev</span></h3>
          <p>Building modern, fast, and scalable web applications with a passion for clean UI & UX.</p>
        </div>

        {/* Navigation Links */}
        <div className="footer-links">
          <h4>Quick Links</h4>
          <ul>
            <li onClick={() => scrollToSection('home')}>Home</li>
            <li onClick={() => scrollToSection('about')}>About</li>
            <li onClick={() => scrollToSection('projects')}>Projects</li>
            <li onClick={() => scrollToSection('contact')}>Contact</li>
          </ul>
        </div>

        {/* Contact Info */}
        <div className="footer-contact">
          <h4>Contact</h4>
          <p><FaEnvelope /> ronaldkimutai48@gmail.com.com</p>
        </div>

        {/* Social Icons */}
        <div className="footer-social">
          <h4>Follow Me</h4>
          <div className="social-icons">
            <a href="https://github.com/Ronaldkim807" target="_blank" rel="noreferrer"><FaGithub /></a>
            <a href="https://linkedin.com/in/ronald-kimutai-172129345/" target="_blank" rel="noreferrer"><FaLinkedin /></a>
            <a href="https://twitter.com/" target="_blank" rel="noreferrer"><FaTwitter /></a>
          </div>
        </div>
      </div>

      {/* Bottom Bar */}
      <div className="footer-bottom">
        <p>&copy; {currentYear} Ronald Kimutai. All rights reserved.</p>
        <button className="back-to-top" onClick={scrollToTop}><FaArrowUp /></button>
      </div>
    </footer>
  );
};

export default Footer;
