import React, { useEffect } from 'react';
import './About.css';
import aboutGif from '../assets/4.gif';
import resumeFile from '../assets/Ronald_Kimutai_Resume.pdf'; // Add your resume file to assets

const About = () => {
  useEffect(() => {
    // Trigger fade-in animations when elements come into view
    const fadeElements = document.querySelectorAll('.fade-in');

    const handleScroll = () => {
      fadeElements.forEach((el) => {
        const rect = el.getBoundingClientRect();
        if (rect.top < window.innerHeight - 100) {
          el.classList.add('visible');
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Run once on page load

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleDownloadResume = () => {
    // Create a temporary anchor element to trigger download
    const link = document.createElement('a');
    link.href = resumeFile;
    link.download = 'Ronald_Kimutai_Resume.pdf';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const handleViewResume = () => {
    // Open resume in a new tab
    window.open(resumeFile, '_blank');
  };

  return (
    <section id="about" className="about">
      <div className="container">
        <h2 className="section-title fade-in">About Me</h2>
        <div className="about-content">
          <div className="about-text fade-in">
            <h3>My Journey in IT</h3>
            <p>
              I'm Ronald (Kiptulwo) Kimutai, a passionate and results-driven Bachelor of Science 
              in Information Technology student at KCA University. I'm deeply enthusiastic about 
              technology, with a strong focus on software development, systems analysis, and 
              innovative IT solutions that solve real-world challenges.
            </p>
            <p>
              My academic background and hands-on experience have equipped me with a solid 
              understanding of programming, databases, networks, and system design principles. 
              I am skilled in various technologies and development tools, and I continuously 
              seek to expand my knowledge in both emerging and established areas of IT.
            </p>
            <p>
              I am a proactive learner, a problem solver by nature, and a strong believer in 
              continuous improvement. Whether working independently or as part of a collaborative 
              team, I value professionalism, integrity, and efficiency in every task I undertake.
            </p>

            {/* Resume Download Section */}
            <div className="resume-section">
              <h4>My Resume</h4>
              <p>Download or view my resume to learn more about my skills and experience:</p>
              <div className="resume-buttons">
                <button className="btn primary" onClick={handleViewResume}>
                  <i className="fas fa-eye"></i> View Resume
                </button>
                <button className="btn secondary" onClick={handleDownloadResume}>
                  <i className="fas fa-download"></i> Download Resume
                </button>
              </div>
            </div>
          </div>

          {/* Skills Section */}
          <div className="skills fade-in">
            <h3>My Skills</h3>
            <div className="skill-bars">
              <div className="skill">
                <span className="skill-name">JavaScript/React</span>
                <div className="skill-bar">
                  <div className="skill-progress" style={{width: '90%'}} data-width="90"></div>
                </div>
              </div>
              <div className="skill">
                <span className="skill-name">HTML/CSS</span>
                <div className="skill-bar">
                  <div className="skill-progress" style={{width: '85%'}} data-width="85"></div>
                </div>
              </div>
              <div className="skill">
                <span className="skill-name">Node.js</span>
                <div className="skill-bar">
                  <div className="skill-progress" style={{width: '80%'}} data-width="80"></div>
                </div>
              </div>
              <div className="skill">
                <span className="skill-name">Database Management</span>
                <div className="skill-bar">
                  <div className="skill-progress" style={{width: '75%'}} data-width="75"></div>
                </div>
              </div>
              <div className="skill">
                <span className="skill-name">PHP/MySQL</span>
                <div className="skill-bar">
                  <div className="skill-progress" style={{width: '85%'}} data-width="85"></div>
                </div>
              </div>
              <div className="skill">
                <span className="skill-name">Python</span>
                <div className="skill-bar">
                  <div className="skill-progress" style={{width: '70%'}} data-width="70"></div>
                </div>
              </div>
            </div>

            {/* Small GIF below skills */}
            <div className="about-gif">
              <img src={aboutGif} alt="About Animation" />
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
