import React, { useEffect, useState } from 'react';
import './About.css';
import resumeFile from '../assets/Ronald_Kimutai_Resume.pdf'; // Your resume file

const About = () => {
  const [currentImage, setCurrentImage] = useState(0);

  // University images
  const uniImages = [
    require('../assets/kca1.png'),
    require('../assets/kca2.png'),
    require('../assets/kca3.png')
  ];

  useEffect(() => {
    // Fade-in animation on scroll
    const fadeElements = document.querySelectorAll('.fade-in');
    const handleScroll = () => {
      fadeElements.forEach((el) => {
        const rect = el.getBoundingClientRect();
        if (rect.top < window.innerHeight - 100) {
          el.classList.add('visible');
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll();

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Change images every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prev) => (prev + 1) % uniImages.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [uniImages.length]);

  const handleDownloadResume = () => {
    const link = document.createElement('a');
    link.href = resumeFile;
    link.download = 'Ronald_Kimutai_Resume.pdf';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const handleViewResume = () => {
    window.open(resumeFile, '_blank');
  };

  return (
    <section id="about" className="about">
      <div className="container">
        {/* Heading Changed */}
        <h2 className="section-title fade-in">Be My Learning</h2>
        <div className="about-content">
          <div className="about-text fade-in">
            <h3>My Journey in IT</h3>
            <p>
              I'm Ronald (Kiptulwo) Kimutai, currently pursuing a Bachelor of Science 
              in Information Technology at KCA University, Main Campus. My passion 
              lies in creating impactful tech solutions, improving user experiences, 
              and leveraging software to solve real-world problems.
            </p>
            <p>
              At KCA University, I’ve gained a solid foundation in programming, 
              networking, database management, and modern web development frameworks. 
              The university’s vibrant learning environment has allowed me to interact 
              with like-minded students, participate in coding hackathons, and work on 
              exciting projects that blend theory with practical skills.
            </p>
            <p>
              My ultimate goal is to specialize in areas like Artificial Intelligence, 
              Cloud Computing, and Cybersecurity, contributing to technological 
              advancements while continuously learning and growing as a professional.
            </p>

            {/* Resume Download Section */}
            <div className="resume-section">
              <h4>My Resume</h4>
              <p>Download or view my resume to learn more about my skills and experience:</p>
              <div className="resume-buttons">
                <button className="btn primary" onClick={handleViewResume}>
                  <i className="fas fa-eye"></i> View Resume
                </button>
                <button className="btn secondary" onClick={handleDownloadResume}>
                  <i className="fas fa-download"></i> Download Resume
                </button>
              </div>
            </div>
          </div>

          {/* Skills Section */}
          <div className="skills fade-in">
            <h3>My Skills</h3>
            <div className="skill-bars">
              {[
                { name: "JavaScript/React", width: "90%" },
                { name: "HTML/CSS", width: "85%" },
                { name: "Node.js", width: "80%" },
                { name: "Database Management", width: "75%" },
                { name: "PHP/MySQL", width: "85%" },
                { name: "Python", width: "70%" },
              ].map((skill, i) => (
                <div className="skill" key={i}>
                  <span className="skill-name">{skill.name}</span>
                  <div className="skill-bar">
                    <div className="skill-progress" style={{ width: skill.width }}></div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* University Section */}
        <div className="university-section fade-in">
          <h3>My University - KCA University, Main Campus</h3>
          <p>Located in Nairobi, KCA University offers a cutting-edge IT program that combines theory with hands-on learning, equipping students for real-world challenges.</p>
          <div className="university-image" style={{ maxWidth: "500px", margin: "0 auto" }}>
            <img
              src={uniImages[currentImage]}
              alt="KCA University"
              style={{ width: "100%", borderRadius: "10px" }}
            />
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
