import React, { useEffect, useState } from 'react';
import './About.css';
import resumeFile from '../assets/Ronald_Kimutai_Resume.pdf'; // keep your resume in assets

const About = () => {
  const [currentImage, setCurrentImage] = useState(0);

  // University images (place your images in src/assets and adjust names if needed)
  const uniImages = [
    require('../assets/kca1.png'),
    require('../assets/kca2.png'),
    require('../assets/kca3.png')
  ];

  // Certifications - update logo paths if you store images under other names
  const certifications = [
    {
      id: 'cisco-net-1',
      logo: require('../assets/cisco.png'),
      title: 'Network Support and Security',
      issuer: 'Cisco Networking Academy',
      issued: 'Sep 2025',
      skills: ['CCNA', 'Network Security', 'Help Desk Support', 'Endpoint Devices', 'Cisco Firewall Security', 'Windows Firewall', 'Cybersecurity'],
      credentialUrl: '#'
    },
    {
      id: 'cisco-py-1',
      logo: require('../assets/cisco.png'),
      title: 'Python Essentials 1',
      issuer: 'Cisco Networking Academy',
      issued: 'Feb 2025',
      skills: ['PostgreSQL', 'Jupyter Notebook', 'SQL Optimization'],
      credentialUrl: '#'
    },
    {
      id: 'cisco-data-1',
      logo: require('../assets/cisco.png'),
      title: 'Data Analytics Essentials',
      issuer: 'Cisco Networking Academy',
      issued: 'Jan 2025',
      skills: ['Data Analysis', 'Excel', 'Basic Statistics'],
      credentialUrl: '#'
    }
  ];

  // Skills for the resume-like section
  const resumeSkills = [
    'JavaScript / React',
    'HTML / CSS',
    'Node.js',
    'Database Management (MongoDB & MySQL)',
    'PHP / MySQL',
    'Python',
    'CCNA',
    'Network Security',
    'Cybersecurity'
  ];

  useEffect(() => {
    // Fade-in on scroll
    const fadeElements = document.querySelectorAll('.fade-in');
    const handleScroll = () => {
      fadeElements.forEach((el) => {
        const rect = el.getBoundingClientRect();
        if (rect.top < window.innerHeight - 100) el.classList.add('visible');
      });
    };
    window.addEventListener('scroll', handleScroll);
    handleScroll();
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Slider auto-change every 5s
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((p) => (p + 1) % uniImages.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [uniImages.length]);

  const handleViewResume = () => {
    window.open(resumeFile, '_blank');
  };

  const handleDownloadResume = () => {
    const a = document.createElement('a');
    a.href = resumeFile;
    a.download = 'Ronald_Kimutai_Resume.pdf';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  return (
    <section id="about" className="about">
      <div className="container">

        {/* Title */}
        <h2 className="section-title fade-in">KCA University</h2>

        {/* About text */}
        <div className="about-content">
          <div className="about-text fade-in">
            <h3>My Journey in IT</h3>
            <p>
              I'm Ronald (Kiptulwo) Kimutai. I study at KCA University (Main Campus) where I am building a strong foundation in software development,
              networking, databases and system design. I enjoy hands-on projects, collaborating on solutions, and learning technologies that solve real problems.
            </p>
            <p>
              At university I've participated in group projects, practical labs and networking workshops that complement classroom theory with real-world practice.
              My current interests include Cloud Computing, Cybersecurity and Data Science.
            </p>

            <div className="resume-section">
              <h4>Resume</h4>
              <div className="resume-buttons">
                <button className="btn primary" onClick={handleViewResume}>View Resume</button>
                <button className="btn secondary" onClick={handleDownloadResume}>Download Resume</button>
              </div>
            </div>
          </div>
        </div>

        {/* University image + caption */}
        <div className="university-section fade-in">
          <div className="university-image">
            <img src={uniImages[currentImage]} alt={`KCA University ${currentImage + 1}`} />
          </div>

          <div className="university-caption">
            <strong>Currently studying</strong>
            <span>•</span>
            <span>Bachelor of Science in Information Technology</span>
            <span>•</span>
            <span>KCA University — Main Campus</span>
          </div>
        </div>

        {/* Certifications (CV-style cards) */}
        <div className="certifications fade-in">
          <h3 className="cert-heading">Licenses & Certifications</h3>

          <div className="cert-cards">
            {certifications.map((c) => (
              <div className="cert-card" key={c.id}>
                <div className="cert-left">
                  <img src={c.logo} alt={`${c.issuer} logo`} className="cert-logo" />
                </div>

                <div className="cert-body">
                  <div className="cert-title">{c.title}</div>
                  <div className="cert-meta">{c.issuer} <span className="dot">•</span> Issued {c.issued}</div>

                  <div className="cert-skills">
                    {c.skills.map((s, i) => (
                      <span className="cert-skill" key={i}>{s}</span>
                    ))}
                  </div>

                  <div className="cert-actions">
                    <a className="credential-link" href={c.credentialUrl} target="_blank" rel="noreferrer">Show credential</a>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Skills (resume-like list + bars) */}
        <div className="skills-section fade-in">
          <h3 className="skills-heading">Skills</h3>

          <div className="skills-resume">
            <ul className="skills-list">
              {resumeSkills.map((s, i) => (
                <li key={i} className="skill-chip">{s}</li>
              ))}
            </ul>

            <div className="skills-bars">
              {[
                { name: 'JavaScript / React', width: '90%' },
                { name: 'HTML / CSS', width: '85%' },
                { name: 'Node.js', width: '80%' },
                { name: 'Database Management', width: '75%' },
                { name: 'CCNA / Network Security', width: '80%' },
                { name: 'Python', width: '70%' }
              ].map((sk, idx) => (
                <div className="skill-row" key={idx}>
                  <div className="skill-row-left">{sk.name}</div>
                  <div className="skill-row-right">
                    <div className="skill-bar">
                      <div className="skill-progress" style={{ width: sk.width }} data-width={parseInt(sk.width, 10)} />
                    </div>
                    <div className="skill-percent">{sk.width}</div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

      </div>
    </section>
  );
};

export default About;
