import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min"; // ✅ Needed for Collapse, Offcanvas, Dropdown
import "./Navbar.css";

const Navbar = () => {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <nav className={`navbar navbar-expand-lg fixed-top ${isScrolled ? "navbar-dark bg-dark shadow-sm" : "navbar-light bg-light"}`}>
      <div className="container">
        {/* Logo */}
        <a className="navbar-brand fw-bold" href="#home" onClick={() => scrollToSection("home")}>
          Ronald<span className="text-primary">.dev</span>
        </a>

        {/* Offcanvas Toggle for Mobile */}
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="offcanvas"
          data-bs-target="#offcanvasNavbar"
          aria-controls="offcanvasNavbar"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        {/* Offcanvas Menu */}
        <div
          className="offcanvas offcanvas-end"
          tabIndex="-1"
          id="offcanvasNavbar"
          aria-labelledby="offcanvasNavbarLabel"
        >
          <div className="offcanvas-header">
            <h5 className="offcanvas-title" id="offcanvasNavbarLabel">Menu</h5>
            <button
              type="button"
              className="btn-close"
              data-bs-dismiss="offcanvas"
              aria-label="Close"
            ></button>
          </div>
          <div className="offcanvas-body">
            <ul className="navbar-nav ms-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <a className="nav-link" href="#home" onClick={() => scrollToSection("home")}>Home</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#about" onClick={() => scrollToSection("about")}>About</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#projects" onClick={() => scrollToSection("projects")}>Projects</a>
              </li>
              <li className="nav-item dropdown">
                <a
                  className="nav-link dropdown-toggle"
                  href="#"
                  role="button"
                  data-bs-toggle="dropdown"
                  aria-expanded="false"
                >
                  More
                </a>
                <ul className="dropdown-menu">
                  <li><a className="dropdown-item" href="#contact" onClick={() => scrollToSection("contact")}>Contact</a></li>
                  <li><a className="dropdown-item" href="#resume">Resume</a></li>
                </ul>
              </li>
            </ul>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
